interface IconProps extends ComponentPropsWithoutRef<'svg'> {
  name: IconName;
  color?: string;
}

const _Icon = ({ name, color = 'currentColor', ...others }: IconProps) => {
  // clone করে color override করা হচ্ছে
  return React.cloneElement(icons[name], {
    ...others,
    fill: color,
  });
};

export const Icon = createPolymorphicComponent<'svg', IconProps>(_Icon);
